export default {
  data() {
    return {
      tableData: [],
      total: 0,
      pageNum: 1
    };
  },

  created() {
    this.load();
  },

  methods: {
    load() {
      //请求分页查询数据
      this.request.get("/user/page", {
        params: {
          pageNum: 1,
          pageSize: 8
        }
      }).then(res => {
        console.log(res);
        this.tableData = res.records;
        this.total = res.total;
      });
    },

    handleCurrentChange(pageNum) {
      this.pageNum = pageNum;
      this.load();
    }

  }
};
