import Vue from 'vue'
import VuRouter from 'vue-router'
import viewHome from '../views/Home/index.vue'
import viewUser from '../views/User/index.vue'
import viewLogin from '../views/Login/index.vue'

Vue.use(VuRouter)

const routes =[
    {
        path:'/',
        component:()=>import("../views/Login/index.vue"),

    },
    {
        path:'/login',
        name:'Login',
        component:()=>import("../views/Login/index.vue"),
    },
    {
        path:'/fail',
        component:()=>import("../views/Login/fail.vue"),
        hiddem:true
    },
    {
        path:'/home',
        name:'Home',
        component:viewHome,
        children:[
            {
                path:'/computer',
                name:'computer',
                component:viewHome,
            },
            {
                path:'/math',
                name:'math',
                component:viewHome,
            }
        ]
    },
    {
        path:'/user',
        name:'User',
        component:viewUser,
        children:[
            {
                path:'/basicInfo',
                name:'basicInfo',
                component:()=>import("../views/User/BasicInfo")
            },
            {
                path:'/subscribeList',
                name:'subscribeList',
            },
            {
                path:'/fanList',
                name:'fanList',
            }
        ]
    }
]

const router = new VuRouter({
    mode:'history',
    scrollBehavior: () => ({ y: 0 }),
    routes
})

export default router
