import CommonHeader from '../../src/components/CommonHeader.vue';
import CommonAside from '../../src/components/CommonAside.vue';
export default {
  name: 'viewHome',
  components: {
    CommonAside: CommonAside,
    CommonHeader: CommonHeader
  },
  data: function data() {
    return {
      postListData: [],
      commentListData: [],
      total: 0,
      pageNum: 1,
      pageSize: 8
    };
  },
  methods: {
    getSection: function getSection(name) {
      var _this = this;

      this.request.get("/section/" + name).then(function (res) {
        localStorage.setItem('section', JSON.stringify(res.data));

        _this.request.get("/post/page", {
          params: {
            pageNum: _this.pageNum,
            pageSize: _this.pageSize,
            sectionId: JSON.parse(localStorage.getItem("section")).id
          }
        }).then(function (res) {
          _this.postListData = res.data.records;
          _this.total = res.data.total;
        });
      });
      this.postListData.forEach(function (element) {
        _this.request.get("/user/" + element.creatorId).then(function (res) {
          element.userName = res.data.username;
        });
      });
    }
  }
};
